mapper.select:
select n.notice_no, n.member_no, n.group_no, n.title, n.create_date, n.view_count, p.notice_photo_no, p.notice_photo_file from nm_notice n
inner join nm_notice_photo p on n.notice_no= p.notice_no
where n.notice_no=#{noticeNo}

select notice_no, member_no, title, create_date, view_count from nm_notice n
inner join nm_notice_photo p on n.notice_no= p.notice_no
where title like #{keywordPattern} or content like #{keywordPattern}
